import axios from "axios";

// API Base URL - Change this to your local server when using MongoDB Compass
const API_BASE_URL = process.env.NODE_ENV === 'production' 
  ? 'https://todo-app-server-eosin.vercel.app/api'
  : 'http://localhost:8080/api';

console.log('🌐 API Base URL:', API_BASE_URL);

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    try {
      const userData = localStorage.getItem('userData');
      if (userData) {
        const parsedUserData = JSON.parse(userData);
        const token = parsedUserData.token;
        if (token) {
          config.headers['x-access-token'] = token;
          config.headers['Authorization'] = `Bearer ${token}`;
        }
      }
    } catch (error) {
      console.error('Error parsing user data from localStorage:', error);
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor to handle auth errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('userData');
      if (typeof window !== 'undefined') {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

// ----------- API FUNCTIONS -----------

export async function registerUser(userData) {
  try {
    const response = await api.post('/users/register', userData);
    return response.data.data || response.data;
  } catch (error) {
    throw new Error(error.response?.data?.message || 'Registration failed');
  }
}

export async function loginUser(userData) {
  try {
    const response = await api.post('/users/login', userData);
    return response.data.data || response.data;
  } catch (error) {
    throw new Error(error.response?.data?.message || 'Login failed');
  }
}

export async function getTodos(userId) {
  try {
    const response = await api.get('/todos', { params: { userId } });
    return response.data.data || response.data;
  } catch (error) {
    throw new Error(error.response?.data?.message || 'Failed to fetch todos');
  }
}

export async function createTodo(todo, userId) {
  try {
    const response = await api.post('/todos', todo, { params: { userId } });
    return response.data.data || response.data;
  } catch (error) {
    throw new Error(error.response?.data?.message || 'Failed to create todo');
  }
}

export async function updateTodo(todo) {
  try {
    todo.completed_time = todo.completed ? Date.now() : null;
    const response = await api.put(`/todos/${todo._id}`, todo);
    return response.data.data || response.data;
  } catch (error) {
    throw new Error(error.response?.data?.message || 'Failed to update todo');
  }
}

export async function deleteTodo(id) {
  try {
    const response = await api.delete(`/todos/${id}`);
    return response.data.data || response.data;
  } catch (error) {
    throw new Error(error.response?.data?.message || 'Failed to delete todo');
  }
}

export async function deleteAllTodos(userId) {
  try {
    const response = await api.delete('/todos/delete/all', { params: { userId } });
    return response.data.data || response.data;
  } catch (error) {
    throw new Error(error.response?.data?.message || 'Failed to delete all todos');
  }
}
